mapper.select:
    SELECT 	IF(PASS = #{LOGIN_PASS},'00','01') AS RSLT_CD 
    FROM 	MEMB_BAS
	WHERE 	MEMB_ID = #{LOGIN_ID}

SELECT * FROM ( SELECT ROW_NUMBER() OVER() AS R_NUM ,Z.MEMB_ID ,Z.CRE_SEQ ,Z.TIT ,Z.CONT ,Z.LIKE_CNT ,DATE_FORMAT(Z.CRE_DAT,'%Y-%m-%d %H:%i') AS CRE_DAT ,B.CODE_VAL AS MEMB_SC_NM ,C.CODE_DESC AS MEMB_DEP_NM ,D.CODE_VAL AS TIT_NM ,A.NICK_NM FROM OPEN_BUB_BAS Z,MEMB_BAS A LEFT JOIN CD_BAS B ON B.CODE_TYPE = 'MEMB_SC_CD' AND B.CODE = A.MEMB_SC_CD LEFT JOIN CD_BAS C ON C.CODE_TYPE = 'MEMB_DEP_CD' AND C.CODE = A.MEMB_DEP_CD LEFT JOIN CD_BAS D ON D.CODE_TYPE = 'TIT_CD' AND D.CODE = A.TIT_CD WHERE Z.MEMB_SC_CD = #{MEMB_SC_CD} AND Z.MEMB_ID = A.MEMB_ID
AND Z.MEMB_DEP_CD = #{MEMB_DEP_CD}
ORDER BY Z.CRE_DAT DESC ) A
WHERE A.R_NUM >= (IF(1 = #{REQ_PAGE},0,#{REQ_PAGE}-1) * IF(1 = #{REQ_PAGE},0,#{LIST_UNIT_CNT})) + 1 AND A.R_NUM <= #{REQ_PAGE} * #{LIST_UNIT_CNT}

    SELECT 
		B.FILE_PATH
		,B.IMG_SEQ
	FROM 	OPEN_BUB_BAS A , OPEN_BUB_IMG_HIST B
	WHERE 	B.CRE_SEQ = A.CRE_SEQ
	AND 	A.MEMB_SC_CD = #{MEMB_SC_CD}

	AND 	A.MEMB_DEP_CD = #{MEMB_DEP_CD}

	AND 	A.CRE_SEQ = #{CRE_SEQ}

mapper.update:


  	UPDATE MEMB_BAS set
		MEMB_SC_CD 		= #{MEMB_SC_CD}
		,MEMB_DEP_CD 	= #{MEMB_DEP_CD}
		,MEMB_NUM 		= #{MEMB_NUM}
		,MEMB_EM 		= #{MEMB_EM}
		,UPD_DAT		= SYSDATE()
	WHERE MEMB_ID = #{MEMB_ID}


  	UPDATE MEMB_BAS set
		PASS 		= #{PASS}
		,UPD_DAT	= SYSDATE()
	WHERE MEMB_ID = #{MEMB_ID}


  	UPDATE MEMB_BAS set
 		NICK_NM 	= #{NICK_NM}
 		,UPD_DAT	= SYSDATE()
	WHERE MEMB_ID = #{MEMB_ID}


  	UPDATE USE_NOTICE_BAS set
		APP_NOTICE_YN 	= #{APP_NOTICE_YN}
		,DEP_NOTICE_YN 	= #{DEP_NOTICE_YN}
		,UPD_DAT		= SYSDATE()
	WHERE MEMB_ID = #{MEMB_ID}



	UPDATE MEMB_BAS set
 		PROF_IMG_PATH = #{PROF_IMG_PATH}
 		,UPD_DAT		= SYSDATE()
	WHERE MEMB_ID = #{MEMB_ID}

	UPDATE OPEN_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}

	UPDATE FREE_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}

	UPDATE QUES_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}

	UPDATE SUG_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}

	UPDATE GALL_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}

	UPDATE MARK_BUB_BAS set
 		 LIKE_CNT		= IFNULL(LIKE_CNT,0) + 1 			
	WHERE MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND   MEMB_DEP_CD 	= #{MEMB_DEP_CD}
	AND   CRE_SEQ 		= #{CRE_SEQ}
