mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER() AS R_NUM ,Z.MEMB_ID ,Z.CRE_SEQ ,Z.TIT ,Z.CONT ,Z.LIKE_CNT ,DATE_FORMAT(Z.CRE_DAT,'%Y-%m-%d %H:%i') AS CRE_DAT ,B.CODE_VAL AS MEMB_SC_NM ,C.CODE_DESC AS MEMB_DEP_NM ,D.CODE_VAL AS TIT_NM ,A.NICK_NM FROM GALL_BUB_BAS Z,MEMB_BAS A LEFT JOIN CD_BAS B ON B.CODE_TYPE = 'MEMB_SC_CD' AND B.CODE = A.MEMB_SC_CD LEFT JOIN CD_BAS C ON C.CODE_TYPE = 'MEMB_DEP_CD' AND C.CODE = A.MEMB_DEP_CD LEFT JOIN CD_BAS D ON D.CODE_TYPE = 'TIT_CD' AND D.CODE = A.TIT_CD WHERE Z.MEMB_SC_CD = #{MEMB_SC_CD} AND Z.MEMB_ID = A.MEMB_ID
AND Z.MEMB_DEP_CD = #{MEMB_DEP_CD}
ORDER BY Z.CRE_DAT DESC ) A
WHERE A.R_NUM >= (IF(1 = #{REQ_PAGE},0,#{REQ_PAGE}-1) * IF(1 = #{REQ_PAGE},0,#{LIST_UNIT_CNT})) + 1 AND A.R_NUM <= #{REQ_PAGE} * #{LIST_UNIT_CNT}

    SELECT 
		B.ANS_MEMB_ID
		,B.ANS_SEQ
		,B.CONT
		,DATE_FORMAT(B.CRE_DAT,'%Y-%m-%d %H:%i') AS CRE_DAT
	FROM GALL_BUB_BAS A , GALL_BUB_ANS_BAS B
	WHERE 	B.CRE_SEQ = A.CRE_SEQ
	AND 	A.MEMB_SC_CD = #{MEMB_SC_CD}

	AND 	A.MEMB_DEP_CD = #{MEMB_DEP_CD}

	AND 	A.CRE_SEQ = #{CRE_SEQ}

mapper.update:
    UPDATE GALL_BUB_BAS SET
    	TIT 	= #{TIT}
		,CONT 	= #{CONT}
		,UPD_DAT		= SYSDATE()
    WHERE 	MEMB_SC_CD 	= #{MEMB_SC_CD}

	AND		MEMB_DEP_CD = #{MEMB_DEP_CD}
	AND		MEMB_ID 	= #{MEMB_ID}

	AND		CRE_SEQ 	= #{CRE_SEQ}

mapper.delete:
    DELETE FROM GALL_BUB_BAS 
    WHERE 	MEMB_SC_CD 	= #{MEMB_SC_CD}

	AND		MEMB_DEP_CD = #{MEMB_DEP_CD}
	AND		MEMB_ID 	= #{MEMB_ID}

	AND		CRE_SEQ 	= #{CRE_SEQ}

mapper.update:
    UPDATE GALL_BUB_ANS_BAS SET
		CONT 	= #{CONT}
		,UPD_DAT		= SYSDATE()
    WHERE 	ANS_SEQ 	= #{ANS_SEQ}
	AND		CRE_SEQ 	= #{CRE_SEQ}

mapper.delete:
DELETE FROM GALL_BUB_ANS_BAS 
WHERE 	CRE_SEQ 	= #{CRE_SEQ}
AND		ANS_SEQ 	= #{ANS_SEQ}

    DELETE FROM GALL_BUB_IMG_HIST 
    WHERE 	CRE_SEQ 	= #{CRE_SEQ}
	AND		IMG_SEQ 	= #{IMG_SEQ}

mapper.select:
    SELECT 	FILE_PATH
    FROM 	GALL_BUB_IMG_HIST 
    WHERE 	CRE_SEQ 	= #{CRE_SEQ}
	AND		IMG_SEQ 	= #{IMG_SEQ}

SELECT 	FILE_PATH
FROM 	GALL_BUB_IMG_HIST 
WHERE 	CRE_SEQ 	= #{CRE_SEQ}
