mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER() AS R_NUM ,MEMB_ID ,CRE_SEQ ,TIT ,CONT ,LIKE_CNT ,DATE_FORMAT(CRE_DAT,'%Y-%m-%d %H:%i') AS CRE_DAT FROM OPEN_BUB_BAS WHERE MEMB_SC_CD = #{MEMB_SC_CD}
AND MEMB_DEP_CD = #{MEMB_DEP_CD}
ORDER BY CRE_DAT DESC ) A
WHERE A.R_NUM >= (IF(1 = #{REQ_PAGE},0,#{REQ_PAGE}-1) * IF(1 = #{REQ_PAGE},0,#{LIST_UNIT_CNT})) + 1 AND A.R_NUM <= #{REQ_PAGE} * #{LIST_UNIT_CNT}

    SELECT 
		B.FILE_PATH
		,B.IMG_SEQ
	FROM OPEN_BUB_BAS A , OPEN_BUB_IMG_HIST B
	WHERE 	B.CRE_SEQ = A.CRE_SEQ
	AND 	A.MEMB_SC_CD = #{MEMB_SC_CD}

	AND 	A.MEMB_DEP_CD = #{MEMB_DEP_CD}

	AND 	A.CRE_SEQ = #{CRE_SEQ}

mapper.update:
    UPDATE OPEN_BUB_BAS SET
    	TIT 	= #{TIT}
		,CONT 	= #{CONT}
    WHERE 	MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND		MEMB_DEP_CD = #{MEMB_DEP_CD}
	AND		MEMB_ID 	= #{MEMB_ID}
	AND		CRE_SEQ 	= #{CRE_SEQ}

mapper.delete:
    DELETE FROM OPEN_BUB_IMG_HIST 
    WHERE 	CRE_SEQ 	= #{CRE_SEQ}
	AND		IMG_SEQ 	= #{IMG_SEQ}

    DELETE FROM OPEN_BUB_BAS 
    WHERE 	MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND		MEMB_DEP_CD = #{MEMB_DEP_CD}
	AND		MEMB_ID 	= #{MEMB_ID}
	AND		CRE_SEQ 	= #{CRE_SEQ}

mapper.select:
    SELECT 	FILE_PATH
    FROM 	OPEN_BUB_IMG_HIST 
    WHERE 	CRE_SEQ 	= #{CRE_SEQ}
	AND		IMG_SEQ 	= #{IMG_SEQ}

SELECT 	FILE_PATH
FROM 	OPEN_BUB_IMG_HIST 
WHERE 	CRE_SEQ 	= #{CRE_SEQ}

SELECT A.YEAR ,A.MONTH ,DATE_FORMAT(A.DAY1,'%d') AS DAY ,SUBSTR(_UTF8'일월화수목금토',A.YOIL,1) AS DAY_DESC ,A.YOIL ,COUNT(B.CRE_SEQ) AS CNT FROM ( WITH RECURSIVE MON_CAL AS ( SELECT 1 AS NO UNION ALL SELECT 1+MON_CAL.NO FROM MON_CAL
WHERE MON_CAL.NO < DATE_FORMAT(LAST_DAY(CURDATE()),'%d')
) SELECT DATE_FORMAT(CURDATE(),'%Y') AS YEAR, DATE_FORMAT(CURDATE(),'%m') AS MONTH, DATE_ADD(DATE_FORMAT(CURDATE(),'%Y-%m-01'),INTERVAL NO-1 DAY) AS DAY1, DAYOFWEEK(DATE_ADD(DATE_FORMAT(CURDATE(),'%Y-%m-01'),INTERVAL NO-1 DAY)) AS YOIL FROM MON_CAL) A LEFT JOIN SCHD_BAS B ON A.DAY1 BETWEEN B.STRT_SCHD_YMD AND B.END_SCHD_YMD AND B.MEMB_ID = #{MEMB_ID} GROUP BY YEAR,MONTH,DAY,YOIL ORDER BY A.DAY1

mapper.update:
    UPDATE SCHD_BAS SET
    	TIT 	= #{TIT}
		,CONT 	= #{CONT}
    WHERE 	MEMB_SC_CD 	= #{MEMB_SC_CD}
	AND		MEMB_DEP_CD = #{MEMB_DEP_CD}
	AND		MEMB_ID 	= #{MEMB_ID}
	AND		CRE_SEQ 	= #{CRE_SEQ}
