mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER() AS R_NUM ,MEMB_ID ,CRE_SEQ ,TIT ,CONT ,LIKE_CNT ,DATE_FORMAT(CRE_DAT,'%Y-%m-%d %H:%i') AS CRE_DAT FROM OPEN_BUB_BAS WHERE MEMB_SC_CD = #{MEMB_SC_CD}
AND MEMB_DEP_CD = #{MEMB_DEP_CD}
ORDER BY CRE_DAT DESC ) A
WHERE A.R_NUM >= (IF(1 = #{REQ_PAGE},0,#{REQ_PAGE}-1) * IF(1 = #{REQ_PAGE},0,#{LIST_UNIT_CNT})) + 1 AND A.R_NUM <= #{REQ_PAGE} * #{LIST_UNIT_CNT}

    SELECT 
		B.FILE_PATH
		,B.IMG_SEQ
	FROM OPEN_BUB_BAS A , OPEN_BUB_IMG_HIST B
	WHERE 	B.CRE_SEQ = A.CRE_SEQ
	AND 	A.MEMB_SC_CD = #{MEMB_SC_CD}

	AND 	A.MEMB_DEP_CD = #{MEMB_DEP_CD}

	AND 	A.CRE_SEQ = #{CRE_SEQ}
